mapper.select:
select * from test_suite

    and id=#{testCase.id}
    and name=#{testCase.name}
    and description=#{testCase.description}
    and status=#{testCase.status}
    and suite_id=#{testCase.suiteId}


    limit #{page.startIndex}, #{page.pageSize}

select * from test_case
where suite_id=#{suiteId}

    limit #{page.startIndex}, #{page.pageSize}

mapper.update:
update test_case
name=#{name}, description=#{description}, priority=#{priority}, status=#{status}, update_time=datetime('now', '+8 hour')
where id=#{id}
