mapper.select:
select * from test_suite

    id=#{testTestCase.id}
    name=#{testTestCase.name}
    description=#{testTestCase.description}
    status=#{testTestCase.status}
    suite_id=#{testTestCase.suiteId}


    limit #{page.startIndex}, #{page.pageSize}

mapper.update:
update test_case
name=#{name}, description=#{description}, priority=#{priority}, status=#{status}, update_time=datetime('now', '+8 hour')
where id=#{id}
