mapper.select:
select * from test_image

    and id=#{testImage.id}
    and path like '%'||#{testImage.path}||'%'
    and similarity=#{testImage.similarity}
    and confidence=#{testImage.confidence}
    and case_id=#{testImage.caseId}


    limit #{page.startIndex}, #{page.pageSize}

mapper.update:
update test_image
path=#{path}, similarity=#{similarity}, confidence=#{confidence}, update_time=datetime('now', '+8 hour')
where id=#{id}
